// import axios from "axios";
import { useState, useEffect, useCallback } from "react";
import { fetchNewDeck, drawCards } from "../API/getRequests";

export interface ICard {
  code: string;
  image: string;
  // value: number;
  images: {
    svg: string;
    png: string;
  };
  value: string;
  suit: string;
}

// interface ICardsInfo {
//   playerCards: {
