  //       currTotal += 10;
  //     } else if (value === "ACE" ) {
  //       console.log('Ace');
  //       // if the value is an ace, by default is equal to 11
  //       currTotal += 11;
  //       acePosition.push(currIndex);
  //     } else {
  //       const NumericVal = Number(value);
  //       currTotal += NumericVal;
  //     }
  //     currIndex += 1;
  //   });

  //   return { currTotal, acePosition, currIndex };

