  //   // }
  // }, [playersCards, totalPlayerInfo]);

  useEffect(() => {
    console.log("playersCards", playersCards);
  }, [playersCards]);

    useEffect(() => {
    console.log("totalPlayerInfo", totalPlayerInfo);
  }, [totalPlayerInfo]);

  // useEffect(() => {
  //   console.log("playersCards", playersCards);
  //   console.log("computersCards", computersCards);
  // }, [computersCards, playersCards]);

  // useEffect(() => {
